    suspend fun seedIfNeeded() {
        // Check if words already exist
        val existingWords = wordDao.getAllWordsForReview(System.currentTimeMillis(), limit = 1)
        if (existingWords.isNotEmpty()) {
            // Words exist, but check if high school levels need to be added
            seedHighSchoolIfNeeded()
            return
        }

        // Clear any existing levels (in case they were created without words)
        // and reseed with proper Japanese levels and words
        seedLevelsAndWords()
    }

    private suspend fun seedHighSchoolIfNeeded() {
        // Check if high school levels already exist
        val allLevels = levelDao.getAllLevelsSync()
        val hasHighSchool = allLevels.any { it.name.contains("高校") }

        if (hasHighSchool) {
            return // High school levels already exist
        }

        // Get the max orderIndex for new levels
        val maxOrderIndex = allLevels.filter { it.parentId == null }.maxOfOrNull { it.orderIndex } ?: 2

        // Add high school levels
        val highSchool1 = Level(name = "高校1年", orderIndex = maxOrderIndex + 1, parentId = null)
        val highSchool2 = Level(name = "高校2年", orderIndex = maxOrderIndex + 2, parentId = null)
