        val highSchool2Id = levelDao.insert(highSchool2)
        val highSchool3Id = levelDao.insert(highSchool3)

        // Create units for High School 1 (高校1年)
        val hs1Unit1 = Level(name = "Unit 1 - 高校基本動詞", orderIndex = 0, parentId = highSchool1Id)
        val hs1Unit2 = Level(name = "Unit 2 - 抽象名詞", orderIndex = 1, parentId = highSchool1Id)
        val hs1Unit3 = Level(name = "Unit 3 - 日常・学校生活", orderIndex = 2, parentId = highSchool1Id)
        val hs1Unit4 = Level(name = "Unit 4 - 感情・性格", orderIndex = 3, parentId = highSchool1Id)
        val hs1Unit5 = Level(name = "Unit 5 - 社会・文化", orderIndex = 4, parentId = highSchool1Id)

        val hs1Unit1Id = levelDao.insert(hs1Unit1)
        val hs1Unit2Id = levelDao.insert(hs1Unit2)
        val hs1Unit3Id = levelDao.insert(hs1Unit3)
        val hs1Unit4Id = levelDao.insert(hs1Unit4)
        val hs1Unit5Id = levelDao.insert(hs1Unit5)

        // Create units for High School 2 (高校2年)
        val hs2Unit1 = Level(name = "Unit 1 - アカデミック動詞", orderIndex = 0, parentId = highSchool2Id)
        val hs2Unit2 = Level(name = "Unit 2 - 科学・技術", orderIndex = 1, parentId = highSchool2Id)
        val hs2Unit3 = Level(name = "Unit 3 - 経済・ビジネス", orderIndex = 2, parentId = highSchool2Id)
        val hs2Unit4 = Level(name = "Unit 4 - 環境・自然", orderIndex = 3, parentId = highSchool2Id)
        val hs2Unit5 = Level(name = "Unit 5 - 論説・議論", orderIndex = 4, parentId = highSchool2Id)

        val hs2Unit1Id = levelDao.insert(hs2Unit1)
        val hs2Unit2Id = levelDao.insert(hs2Unit2)
        val hs2Unit3Id = levelDao.insert(hs2Unit3)
        val hs2Unit4Id = levelDao.insert(hs2Unit4)
        val hs2Unit5Id = levelDao.insert(hs2Unit5)

        // Create units for High School 3 (高校3年)
        val hs3Unit1 = Level(name = "Unit 1 - 入試頻出動詞", orderIndex = 0, parentId = highSchool3Id)
        val hs3Unit2 = Level(name = "Unit 2 - 入試頻出名詞", orderIndex = 1, parentId = highSchool3Id)
        val hs3Unit3 = Level(name = "Unit 3 - 入試頻出形容詞", orderIndex = 2, parentId = highSchool3Id)
        val hs3Unit4 = Level(name = "Unit 4 - 学術・専門語彙", orderIndex = 3, parentId = highSchool3Id)
        val hs3Unit5 = Level(name = "Unit 5 - 難関大レベル", orderIndex = 4, parentId = highSchool3Id)

